from rest_framework import serializersfrom .models import *class NoutbuklarSerializer(serializers.Serializer):    brend = serializers.CharField(max_length=120)    memory = serializers.CharField()    type_id = serializers.IntegerField()    def create(self, validated_data):        return Noutbuklar.objects.create(**validated_data)    def update(self, instance, validated_data):        instance.brend = validated_data.get('brend', instance.brend)        instance.memory = validated_data.get('memory', instance.memory)        instance.type_id = validated_data.get('type_id', instance.type_id)        instance.save()        return instanceclass Aperatsion_tizimSerializer(serializers.Serializer):    type = serializers.CharField()    def create(self, validated_data):        return Aperatsion_tizim.objects.create(**validated_data)    def update(self, instance, validated_data):        instance.type = validated_data.get('type', instance.type)        instance.save()        return instance